    // 	filterSplit = "Filter=Yes";
    // }
    // if (splt && fltr){
    // 	filterSplit = "Splits=Yes|Filter=Yes";
    // }
    // if (splt === undefined && fltr === undefined){
    // 	filterSplit = "notapplicable";
    // }
    return obj
