            var char = $scope.currentFilter;
            var pattern = "^" + char.toUpperCase();
            var regex = new RegExp(pattern);
            if (l.DISPLAY_LAST_COMMA_FIRST.match(regex)) {
                return l.DISPLAY_LAST_COMMA_FIRST
            }
